import React from 'react'

const Header = (props) => {
  return (
    <>
      <h1>
        {props.course}
      </h1>
    </>
  )
}

const Content = (props) => {
  return (
    <>
      <p>
        {props.part} {props.exercises}
      </p>
    </>
  )
}

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}
      </p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part = ['Fundamentals of React', 'Using props to pass data', 'State of a component']
  const exercises = [10, 7, 14]

  return (
    <div>
      <Header course={course} />
      <Content part={part[0]} exercises={exercises[0]} />
      <Content part={part[1]} exercises={exercises[1]} />
      <Content part={part[2]} exercises={exercises[2]} />
      <Total exercises1={exercises[0]} exercises2={exercises[1]} exercises3={exercises[2]} />

    </div>
  )
}

export default App
