const Header = (props) => {
  return (
    <>
      <h1>{props.name}</h1>
    </>
  )
}

const Content = (props) => {
  return (
    <>
      <Parts part1= {props.part1} part2={props.part2} part3={props.part3} exercises1={props.exercises1} exercises2={props.exercises2} exercises3={props.exercises3}></Parts>
    </>
  )
}

const Total = (props) => {
  return (
    <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
  )
 }

const Parts = (props) => {
  return (
    <>
      <Part partNumber={props.part1} exercises={props.exercises1}></Part>
      <Part partNumber={props.part2} exercises={props.exercises2}></Part>
      <Part partNumber={props.part3} exercises={props.exercises3}></Part>
    </>
  )
}

const Part = (props) => {
  return (
    <p>
      {props.partNumber}  {props.exercises}
    </p>
  )
}

const App = () => {
  const name = 'Half Stack application development'
  const part1 = {
    name : 'Fundamentals of React',
    exercises : 10
  }
  const part2 = {
    name : 'Using props to pass data',
    exercises : 7
  }
  const part3 = {
    name : 'State of a component',
    exercises : 14
  }
  return (
    <div>
      <Header name={name}></Header>
      <Content part1={part1.name} part2={part2.name} part3={part3.name} exercises1={part1.exercises} exercises2={exercises2} exercises3={exercises3}></Content>
      <Total exercises1={exercises1} exercises2={exercises2} exercises3={exercises3}></Total>
    </div>
  )
}

export default App
