  return (
    <>
      <h1>{props.name}</h1>
    </>
  )
}

const Content = (props) => {
  return (
    <>
      <Part partNumber={props.part1.name} exercises={props.part1.exercises}></Part>
      <Part partNumber={props.part2.name} exercises={props.part1.exercises}></Part>
      <Part partNumber={props.part3.name} exercises={props.part1.exercises}></Part>
    </>
  )
}

const Total = (props) => {
  return (
    <p>Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}</p>
  )
 }

const Part = (props) => {
  return (
    <p>
      {props.partNumber}  {props.exercises}
    </p>
  )
}

const App = () => {
  const name = 'Half Stack application development'
  const parts = [
    {
    name : 'Fundamentals of React',
    exercises : 10
    },
    {
    name : 'Using props to pass data',
    exercises : 7
    },
    {
    name : 'State of a component',
    exercises : 14
    }
  ]

  return (
    <div>
      <Header name={name}></Header>
      <Content part1={parts[0]} part2={parts[1]} part3={parts[2]}></Content>
      <Total exercises1={parts[0].exercises} exercises2={part.exercises} exercises3={part1.exercises}></Total>
    </div>
  )
}

export default App
