import React from 'react'

const Part = (props) => {
  return (
    <p>
      {props.part} {props.exercises}
    </p>
  )
}

const Header = (props) => {
  return (
    <>
      <h1>
        {props.course}
      </h1>
    </>
  )
}

const Content = (props) => {
  return (
    <>
      <Part part1={props.part[0]} exercises={props.exercises[0]} />
      <Part part2={props.part[1]} exercises={props.exercises[1]} />
      <Part part3={props.part[2]} exercises={props.exercises[2]} />
    </>
  )
}

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises {props.exercises1 + props.exercises2 + props.exercises3}
      </p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course} />
      <Content part1={part1} part2={part2} part1={part1}/>
      <Total exercises1={exercises[0]} exercises2={exercises[1]} exercises3={exercises[2]} />

    </div>
  )
}

export default App
